package com.jb.coupon_system_2.data.entity;import org.springframework.boot.autoconfigure.domain.EntityScan;import java.util.UUID;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@NoArgsConstructor@AllArgsConstructor@Builder@Getter@Setter@EntityScanpublic class Company {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private UUID companyId;    @Column(nullable = false)    private String name;    @Column(nullable = false, unique = true)    private String email;    @Column(nullable = false)    private String password;    public UUID getId() {        return companyId;    }}