package com.jb.coupon_system_2.data.entity;import java.util.UUID;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;@Entitypublic class Company {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private UUID id;    @Column(nullable = false)    private String name;    @Column(nullable = false, unique = true)    private String email;    @Column(nullable = false)    private String password;    public UUID getId() {        return id;    }}