package com.jb.coupon_system_2.repository;import com.jb.coupon_system_2.data.entity.Company;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;import java.util.UUID;@Repositorypublic interface CompanyRepository extends JpaRepository<Company, UUID> {    Optional<Company> findCompanyByEmailAndPassword(String email, String password);    void deleteCouponByName(String name);}