package com.jb.coupon_system_2.repository;import com.jb.coupon_system_2.data.dto.CouponDto;import com.jb.coupon_system_2.data.entity.Company;import com.jb.coupon_system_2.data.entity.Coupon;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;import java.util.UUID;@Repositorypublic interface CompanyRepository extends JpaRepository<Company, UUID> {    Optional<Coupon> findCoupon(UUID id);    Optional<Company> findCompany(String email, String password);    Optional<CouponDto> createCoupon(UUID id, CouponDto couponDto);    void deleteCoupon(String name);    Optional<CouponDto> updateCouponQuantity(String name, int newQuantity);}