package com.jb.coupon_system_2.repository;import com.jb.coupon_system_2.data.entity.Coupon;import com.jb.coupon_system_2.data.entity.Customer;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;import java.util.UUID;@Repositorypublic interface CustomerRepository extends JpaRepository<Customer, Long> {    Optional<Customer> findCustomer(String email, String password);    List<Coupon> findPurchasedCouponsExpiringInDays(UUID id, int days);    List<Coupon> findAllCouponsPurchasedByCustomerId(UUID id);    List<Coupon> findAllCouponsUnpurchasedByCustomerId(UUID id);    Coupon purchaseCoupon(UUID id);}