package com.jb.coupon_system_2.repository;import com.jb.coupon_system_2.data.dto.CouponDto;import com.jb.coupon_system_2.data.entity.Coupon;import com.jb.coupon_system_2.data.entity.Customer;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;import java.util.UUID;@Repositorypublic interface CustomerRepository extends JpaRepository<Customer, UUID> {    Optional<Customer> findCustomer(String email, String password);    List<Coupon> findPurchasedCouponsExpiringInDays(UUID couponId, int days);    List<Coupon> findAllCouponsPurchasedByCustomerId(UUID customerId);    List<Coupon> findAllCouponsUnpurchasedByCustomerId(UUID customerId);    CouponDto purchaseCoupon(UUID id);}