package com.jb.coupon_system_2.service;import com.jb.coupon_system_2.data.entity.Coupon;import com.jb.coupon_system_2.repository.CouponRepository;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;import java.time.LocalDate;import java.util.List;@Servicepublic class CouponServiceImpl implements CouponService {    private final CouponRepository couponRepository;    public CouponServiceImpl(CouponRepository couponRepository) {        this.couponRepository = couponRepository;    }    public void deleteExpiredCoupons() {        LocalDate yesterday = LocalDate.now().minusDays(1);        List<Coupon> expiredCoupons = couponRepository.findByExpirationDate(yesterday);        couponRepository.deleteAll(expiredCoupons);    }    @Scheduled(cron = "0 0 0 * * *")    public void scheduledCouponDeletion() {        deleteExpiredCoupons();    }}