package com.jb.coupon_system_2.session;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ClientSessionCleanup {    @Autowired    private ClientSessionRepository clientSessionRepository;    @Scheduled(fixedDelayString = "${cleanupInterval}")    public void cleanupExpiredSessions() {        List<ClientSession> expiredSessions = clientSessionRepository.findExpiredSessions();        clientSessionRepository.deleteAll(expiredSessions);    }}